import React from "react";
import styled from "styled-components";
import Typewriter from "typewriter-effect/dist/core";
import image from "../../images/home.png";
import "./Hero.css";

const Hero = () => {
  var typewriter = new Typewriter(app, {
    loop: true,
    delay: 75,
    onCreateTextNode: customNodeCreator,
  });

  return (
    <div className="container grid grid-two-column">
      <div className="section-hero-data">
        <h1 className="hero-heading">
          C<span>IV</span>F
        </h1>
        <p className="hero-top-data">
          CHARUSAT <span> Innovative Ventures </span> Foundation{" "}
        </p>
        <p className="hero-para">
          typewriter .typeString('CHARUSAT Innovative Ventures Foundation (CIVF)
          is an Accelerator facility with a wide network that provides
          connections with investors and successful entrepreneurs who are not
          only willing to invest their money but also their time helping
          enterprises to flourish in the long run. They also add value by
          providing mentoring and advice along with access to its entire
          network') .pauseFor(300) .start();
          {/* <Typewriter
            loop
            cursor
            cursorStyle="_"
            typeSpeed={70}
            deleetSpeed={50}
            delaySpeed={1000}
            words={
              "."
            }
          /> */}
        </p>
        {/* <Button className="btn hireme-btn">
            <NavLink to="/contact"> hire me </NavLink>
          </Button> */}
      </div>

      {/* for image  */}
      <div className="section-hero-image">
        <picture>
          <img src={image} alt="hero image" className="hero-img " />
        </picture>
      </div>
    </div>
  );
};

export default Hero;
