import image from "../../images/home.png";
import "./Hero.css";

const Hero = () => {
  return (
    // <Wrapper>
    <div className="container grid grid-two-column">
      <div className="section-hero-data">
        <h1 className="hero-heading">CIVF</h1>
        <p className="hero-top-data">
          CHARUSAT Innovative Ventures Foundation{" "}
        </p>
        <p className="hero-para">
          CHARUSAT Innovative Ventures Foundation (CIVF) is an Accelerator
          facility with a wide network that provides connections with investors
          and successful entrepreneurs who are not only willing to invest their
          money but also their time helping enterprises to flourish in the long
          run. They also add value by providing mentoring and advice along with
          access to its entire network.
        </p>
        {/* <Button className="btn hireme-btn">
            <NavLink to="/contact"> hire me </NavLink>
          </Button> */}
      </div>

      {/* for image  */}
      <div className="section-hero-image">
        <picture>
          <img src={image} alt="hero image" className="hero-img " />
        </picture>
      </div>
    </div>
  );
};

